package day9;

public class SwitchEx {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		byte b = 10;
		
		switch(b)
		{
			case 10:
			System.out.println("Hello bro");
			break;
			case 70:
			System.out.println("yo");
			break;
			default:
			System.out.println("Damn");
			
		}
		
		
		
		
		
	}

}
